import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();

  return (
    <header className="bg-gray-800 shadow-lg">
      <nav className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center">
          <Link to="/" className="text-2xl font-bold text-blue-400">
            Tony<span className="text-blue-300">.</span>
          </Link>
          <div className="space-x-6">
            <Link
              to="/"
              className={`hover:text-blue-400 transition-colors ${
                location.pathname === '/' ? 'text-blue-400' : 'text-gray-300'
              }`}
            >
              Home
            </Link>
            <Link
              to="/about"
              className={`hover:text-blue-400 transition-colors ${
                location.pathname === '/about' ? 'text-blue-400' : 'text-gray-300'
              }`}
            >
              About
            </Link>
            <Link
              to="/projects"
              className={`hover:text-blue-400 transition-colors ${
                location.pathname === '/projects' ? 'text-blue-400' : 'text-gray-300'
              }`}
            >
              Projects
            </Link>
            <Link
              to="/blog"
              className={`hover:text-blue-400 transition-colors ${
                location.pathname === '/blog' ? 'text-blue-400' : 'text-gray-300'
              }`}
            >
              Blog
            </Link>
            <Link
              to="/contact"
              className={`hover:text-blue-400 transition-colors ${
                location.pathname === '/contact' ? 'text-blue-400' : 'text-gray-300'
              }`}
            >
              Contact
            </Link>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
