import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const Projects = () => {
  const sectionRef = useRef(null);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('active');
          }
        });
      },
      { threshold: 0.1 }
    );

    const reveals = sectionRef.current.querySelectorAll('.reveal');
    reveals.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        // Try to fetch pinned repos from GitHub
        const githubResponse = await axios.get('https://api.github.com/users/tony-tech-web/repos', {
          params: {
            sort: 'updated',
            per_page: 6
          }
        });

        // Filter for featured repos (less strict filtering)
        const featuredRepos = githubResponse.data.filter(repo =>
          !repo.fork && !repo.archived
        ).slice(0, 6);

        // If no repos found, use some default ones
        const reposToUse = featuredRepos.length > 0 ? featuredRepos : [
          {
            id: 1,
            name: 'MyPortfolio',
            description: 'This is my work, and this is me, HIRE ME!!!',
            html_url: 'https://github.com/tony-tech-web/MyPortfolio',
            language: 'HTML',
            stargazers_count: 1,
            forks_count: 0,
            updated_at: new Date().toISOString()
          },
          {
            id: 2,
            name: 'Nexus',
            description: 'A modern web application',
            html_url: 'https://github.com/tony-tech-web/Nexus',
            language: 'TypeScript',
            stargazers_count: 1,
            forks_count: 0,
            updated_at: new Date().toISOString()
          }
        ];

        // Transform GitHub data to match our project format
        const projects = reposToUse.map(repo => ({
          id: repo.id,
          title: repo.name.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
          description: repo.description || 'No description available',
          technologies: extractTechStack(repo),
          github_url: repo.html_url,
          live_url: extractLiveUrl(repo),
          image_url: null, // GitHub repos don't have preview images
          language: repo.language,
          stars: repo.stargazers_count,
          forks: repo.forks_count,
          updated_at: repo.updated_at,
          isGitHub: true
        }));

        setProjects(projects);
      } catch (error) {
        console.error('Error fetching GitHub repos:', error);
        // Fallback to some default projects if GitHub API fails
        setProjects([
          {
            id: 1,
            title: 'Virtual Personal Stylist',
            description: 'A JavaFX desktop application for outfit selection based on weather and preferences.',
            technologies: ['Java', 'JavaFX', 'Weather API'],
            github_url: 'https://github.com/tony-tech-web/virtual-stylist',
            live_url: null,
            image_url: null,
            language: 'Java',
            stars: 12,
            forks: 3,
            isGitHub: false
          },
          {
            id: 2,
            title: 'Blockchain Certificate Verifier',
            description: 'A secure Java-based system for academic certificate authentication using blockchain.',
            technologies: ['Java', 'Blockchain', 'Security'],
            github_url: 'https://github.com/tony-tech-web/blockchain-verifier',
            live_url: null,
            image_url: null,
            language: 'Java',
            stars: 8,
            forks: 2,
            isGitHub: false
          },
          {
            id: 3,
            title: 'Attendance System',
            description: 'An automated attendance tracking tool using Streamlit and QR codes.',
            technologies: ['Python', 'Streamlit', 'QR Codes'],
            github_url: 'https://github.com/tony-tech-web/attendance-system',
            live_url: null,
            image_url: null,
            language: 'Python',
            stars: 15,
            forks: 5,
            isGitHub: false
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    const extractTechStack = (repo) => {
      // Extract technologies from repo topics or languages
      const topics = repo.topics || [];
      if (topics.length > 0) {
        return topics.slice(0, 4).map(topic =>
          topic.charAt(0).toUpperCase() + topic.slice(1)
        );
      }

      // Fallback: try to infer from language
      const language = repo.language;
      if (language) {
        return [language];
      }

      return ['Code'];
    };

    const extractLiveUrl = (repo) => {
      // Check if repo has a homepage or demo URL
      if (repo.homepage && repo.homepage !== repo.html_url) {
        return repo.homepage;
      }
      return null;
    };

    fetchProjects();
  }, []);

  return (
    <section id="projects" ref={sectionRef} className="py-20">
      <div className="container mx-auto px-6">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-4xl md:text-5xl font-bold text-center mb-16 reveal">
            Featured <span className="gradient-text">Projects</span>
          </h2>

          {loading ? (
            <div className="text-center py-20">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-400"></div>
              <p className="mt-4 text-gray-400">Loading projects...</p>
            </div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {projects.map((project, index) => (
                <div
                  key={project.id}
                  className="glass-card p-6 hover:scale-105 transition-transform duration-300 reveal"
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  {project.image_url && (
                    <div className="mb-4 overflow-hidden rounded-lg">
                      <img
                        src={project.image_url}
                        alt={project.title}
                        className="w-full h-48 object-cover hover:scale-110 transition-transform duration-300"
                      />
                    </div>
                  )}
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-xl font-semibold">{project.title}</h3>
                    {project.language && (
                      <span className="px-2 py-1 bg-gray-700 text-xs rounded text-gray-300">
                        {project.language}
                      </span>
                    )}
                  </div>
                  <p className="text-gray-300 mb-4 line-clamp-3">{project.description}</p>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map(tech => (
                      <span key={tech} className="px-2 py-1 bg-blue-600/20 text-blue-400 rounded text-sm">
                        {tech}
                      </span>
                    ))}
                  </div>
                  {project.isGitHub && (
                    <div className="flex items-center space-x-4 mb-4 text-sm text-gray-400">
                      {project.stars !== undefined && (
                        <span className="flex items-center">
                          <i className="fas fa-star mr-1 text-yellow-400"></i>
                          {project.stars}
                        </span>
                      )}
                      {project.forks !== undefined && (
                        <span className="flex items-center">
                          <i className="fas fa-code-branch mr-1"></i>
                          {project.forks}
                        </span>
                      )}
                      {project.updated_at && (
                        <span>
                          Updated {new Date(project.updated_at).toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  )}
                  <div className="flex space-x-4">
                    {project.github_url && (
                      <a
                        href={project.github_url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-400 hover:text-blue-300 transition-colors"
                      >
                        <i className="fab fa-github mr-1"></i>Code
                      </a>
                    )}
                    {project.live_url && (
                      <a
                        href={project.live_url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-400 hover:text-blue-300 transition-colors"
                      >
                        <i className="fas fa-external-link-alt mr-1"></i>Live Demo
                      </a>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          <div className="text-center mt-12 reveal">
            <p className="text-gray-400">
              More projects available on{' '}
              <a
                href="https://github.com/tony-tech-web"
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-400 hover:text-blue-300"
              >
                GitHub
              </a>
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
